a']['courses'];		if( $result ) {			//while ($record = $result->fetch_assoc()){			foreach($result as $record){	                            echo 'maps_marker("' . $record["name"] . '",' . $record["Breitengrad"] . ',' . $record["Laengengrad"] . ');';			}			}	?>  }google.maps.event.addDomListener(window, 'load', initialize);</script>