<script src="https://ajax.googleapis.com/ajax/libs/jquery/1.12.2/jquery.min.js"></script><script src="http://maps.googleapis.com/maps/api/js"></script><div class="container-fluid">  <h2>Anlagen</h2><?php$result = getCourses()['data']['courses'];if( $result ) {    echo '<div class="panel-group" id="accordion">';    foreach ($result as $record){        //print ('<li>' . $record['name'] . '</li>');        $cleanedName = str_replace(' ', '', $record['name']);        echo '            <div class="panel panel-default">                    <div class="panel-heading">                    <h4 class="panel-title">                            <a data-toggle="collapse" data-parent="#accordion" href="#'.$cleanedName.'">                            '.$record['name'].'</a>                    </h4>                    </div>            <div id="'.$cleanedName.'" class="panel-collapse collapse">                    <div class="panel-body">' .  $record['info'] .                    '</div>            </div>            </div>        ';    }    echo '</div>';} else {    echo "Query gescheitert.<br/>\n";}?><div class="row">  <div class="col-sm-1"></div>  <div class="col-sm-10"><div id="googleMap" style="width:100%;height:500px;"></div></div>  <div class="col-sm-1"></div></div></div><script>function maps_marker(name, Breitengrad, Laengengrad){		var course_pos = new google.maps.LatLng(Breitengrad,Laengengrad);	var marker1 = new google.maps.Marker({		position: course_pos,		map: map,		title: name		});		console.log(marker1);		marker1.addListener('click', function() {    map.setZoom(12);    map.setCenter(marker1.getPosition());	});	}	function initialize() {		var myLatlng = new google.maps.LatLng(51.0699989,10.9667068);	var mapOptions = {		zoom:6,		center: myLatlng,		mapTypeId:google.maps.MapTypeId.ROADMAP	};  map = new google.maps.Map(document.getElementById("googleMap"),mapOptions);	<?php		$result = getCourses();		if( $result ) {			while ($record = $result->fetch_assoc()){							echo 'maps_marker("' . $record["name"] . '",' . $record["Breitengrad"] . ',' . $record["Laengengrad"] . ');';			}			}	?>  }google.maps.event.addDomListener(window, 'load', initialize);</script>